<?php//--------------- Adding Latest jQuery------------//function wpt_jquery() {	wp_enqueue_script('jquery');}add_action('init', 'wpt_jquery');//-------------- include js files---------------//function wpt_scripts() {	if (!is_admin()) {			wp_register_script('wpt-cycle-two', plugins_url('assets/js/jquery.cycle2.min.js', __FILE__),array('jquery'),'2.1.6', true);				wp_register_script('wpt-cycle-two-caro', plugins_url('assets/js/jquery.cycle2.carousel.js', __FILE__),array('jquery'),'20130528', true);		wp_enqueue_script('wpt-cycle-two');		wp_enqueue_script('wpt-cycle-two-caro');	}}add_action( 'wp_enqueue_scripts', 'wpt_scripts' ); //------------ include css files-----------------//function wpt_style() {	if (!is_admin()) {		wp_register_style('wpt-custom-style', plugins_url('assets/css/custom.css', __FILE__),'','2.0.0', false);				wp_enqueue_style('wpt-custom-style');	}}add_action( 'init', 'wpt_style' );